import React, { Component } from 'react';
import classes from './App.css';
import Recipe from './Recipe/Recipe';

class App extends Component{

  state = {
    recipes: [
      {name: 'Andrew', age: 19, uID: 'hwef73qgwe'}, 
      {name: 'Harris', age: 20, uID: 'hqk78f74f2'} 
    ],
    showRecipes: false
  };

  deleteRecipeHandler = (index) => {
    const recipes = [...this.state.recipes];
    recipes.splice(index, 1);
    this.setState({recipes: recipes})
  }

  toggleRecipesHandler = () => {
    const currentShow = this.state.showRecipes
    this.setState({
      showRecipes: !currentShow
    });
  }

  nameChangedHandler = (event, id) => {
    const recipeIndex = this.state.recipes.findIndex(p => {
      return p.uID === id;
    });
    const recipe = {
      ...this.state.recipes[recipeIndex]
    };

    recipe.name = event.target.value;

    //now update array at index
    const recipes = [...this.state.recipes];
    recipes[recipeIndex] = recipe;

    //then set the state
    this.setState({recipes: recipes});
  }

  render() {
    let recipes = null;
    let btnClass = '';

    if (this.state.showRecipes) {
      recipes = (
        <div>
          {this.state.recipes.map((recipe, index) => { 
          return <Recipe 
            click={() => this.deleteRecipeHandler(index)}
            name={recipe.name} 
            age={recipe.age}
            key={recipe.uID}
            changed={(event) => this.nameChangedHandler(event, recipe.uID)}
            />
          })}
        </div>
      );

      btnClass = classes.Red

    }

    const assignedClasses = [];
    if (this.state.recipes.length <= 2) {
      assignedClasses.push(classes.red); // classes = ['red']
    }
    if(this.state.recipes.length <= 1) {
      assignedClasses.push(classes.bold); //classes = ['red', 'bold']
    }

    return(
      <div className={classes.App}>
        <h1>First React App</h1>
        <input type="text" value="enter ingredients" />
        <p className={assignedClasses.join(' ')}>Testing changing class names</p>
        <button
          className={btnClass}
          onClick={this.toggleRecipesHandler}
        >Toggle People</button>
        {recipes}
      </div> 
    );
  }
};

export default App;
